import React, { useState } from 'react';
import { YMaps, Map, Placemark } from '@pbe/react-yandex-maps';
import { SafeAreaView, View, Modal, TouchableOpacity, Text } from 'react-native';
import { Title } from 'react-native-paper';
import { homeStyle } from './home.style';

const HomeScreen = () => {
  const [modalVisible, setModalVisible] = useState(false);
  const [selectedPoint, setSelectedPoint] = useState({});

  const handlePlacemarkClick = (e, name) => {
    e.preventDefault();
    setSelectedPoint({ name });
    setModalVisible(true);
  };

  const handleModalClose = () => {
    setModalVisible(false);
  };

  const points = [
    { coordinates: [54.738276, 20.489808], name: "Point A" },
    { coordinates: [54.753289, 20.516721], name: "Point B" },
    { coordinates: [54.726419, 20.502906], name: "Point C" },
    { coordinates: [54.743931, 20.458915], name: "Point D" },
    { coordinates: [54.709743, 20.500256], name: "Point E" }
  ];

  const placemarks = points.map((point, index) => (
    <Placemark
      key={index}
      geometry={point.coordinates}
      properties={{ balloonContent: point.name }}
      onClick={(e) => handlePlacemarkClick(e, point.name)}
    />
  ));

  return (
    <SafeAreaView style={homeStyle.page}>
      <Title>Map</Title>
      <YMaps 
        enterprise
        query={{
          load: "package.full", 
          apikey: "af7c0f53-a625-45b6-9e95-fd90a65132des"
        }}>
        <Map
          style={homeStyle.container}
          defaultState={{ center: [54.738276, 20.489808], zoom: 11 }}
        >
          {placemarks}
        </Map>
      </YMaps>
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={handleModalClose}>
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
          <View style={{ backgroundColor: 'white', padding: 20 }}>
            <Text style={{ textAlign: 'center' }}>Placemark {selectedPoint.name} clicked.</Text>
            <TouchableOpacity style={{ marginTop: 20 }} onPress={handleModalClose}>
              <Text style={{ color: 'blue', textAlign: 'center' }}>Видеотрансляция</Text>
            </TouchableOpacity>
            <TouchableOpacity style={{ marginTop: 20 }} onPress={handleModalClose}>
              <Text style={{ color: 'blue', textAlign: 'center' }}>Открыть отчет</Text>
            </TouchableOpacity>
            <TouchableOpacity style={{ marginTop: 20 }} onPress={handleModalClose}>
              <Text style={{ color: 'blue', textAlign: 'center' }}>Закрыть</Text>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>
    </SafeAreaView>
  );
};

export default HomeScreen;
