import React, { useState } from 'react';
import { YMaps, Map, Placemark } from '@pbe/react-yandex-maps';
import { SafeAreaView, View, Modal, TouchableOpacity, Text } from 'react-native';
import { Title } from 'react-native-paper';
import { homeStyle } from './home.style';
import blue from '../../files/blue.png';
import red from '../../files/red.png';

const HomeScreen = () => {
  const [modalVisible, setModalVisible] = useState(false);
  const [modalVideoVisible, setModalVideoVisible] = useState(false);
  const [selectedPoint, setSelectedPoint] = useState({});

  const handlePlacemarkClick = (e, name) => {
    e.preventDefault();
    setSelectedPoint({ name });
    setModalVisible(true);
  };

  const handleModalClose = () => {
    setModalVisible(false);
  };

  const handleModalVideoClose = () => {
    setModalVideoVisible(false);
  };

  const handleModalVideo = () => {
    setModalVideoVisible(true);
  };

  const points = [
    { coordinates: [54.738276, 20.489808], name: "Point A", icon: blue},
    { coordinates: [54.753289, 20.516721], name: "Point B", icon: red },
    { coordinates: [54.726419, 20.502906], name: "Point C", icon: red },
    { coordinates: [54.743931, 20.458915], name: "Point D", icon: blue },
    { coordinates: [54.709743, 20.500256], name: "Point E", icon: blue }
  ];

  const placemarks = points.map(({coordinates, name, icon}, index) => (
    <Placemark
      key={index}
      geometry={coordinates}
      properties={{ 
        balloonContent: name,
      }}
      options={{
        preset: 'islands#icon',
        iconLayout: 'default#image',
        iconImageHref: icon
      }}

      onClick={(e) => handlePlacemarkClick(e, name)}
    />
  ));

  const mapState = {
    center: [54.738276, 20.489808],
    zoom: 11,
    type: 'yandex#map',
    layers: [
      {
        type: 'yandex#map',
        options: {
          // Add a layer with different colors
          trafficControlSize: 'large',
          trafficColorScheme: 'pastel'
        }
      }
    ]
  };

  return (
    <SafeAreaView style={homeStyle.container}>
      <Title style={homeStyle.title}>Yandex Maps Demo</Title>
      <View style={homeStyle.mapContainer}>
        <YMaps>
          <Map state={mapState}>{placemarks}</Map>
        </YMaps>
      </View>
      <Modal visible={modalVisible} animationType="slide" onRequestClose={handleModalClose}>
        <TouchableOpacity onPress={handleModalClose}>
          <Text>Close modal</Text>
        </TouchableOpacity>
      </Modal>
      <Modal visible={modalVideoVisible} animationType="slide" onRequestClose={handleModalVideoClose}>
        <TouchableOpacity onPress={handleModalVideoClose}>
          <Text>Close video modal</Text>
        </TouchableOpacity>
      </Modal>
    </SafeAreaView>
  );
};

export default HomeScreen;
